import React, { useState } from "react";
import Map from "./Map";
import { Layers, TileLayer, VectorLayer } from "./Layers";
import { Style, Icon } from "ol/style";
import Feature from "ol/Feature";
import Point from "ol/geom/Point";
import { osm, vector, wms } from "./Source";
import { fromLonLat, get } from "ol/proj";
import GeoJSON from "ol/format/GeoJSON";
import { Controls, FullScreenControl } from "./Controls";
import FeatureStyles from "./Features/Styles";
import Background from './assets/Ellipse_Head@3x.png';
import mapConfig from "./config.json";
import Drawer from '@mui/material/Drawer';
import Checkbox from '@mui/material/Checkbox';
import Paper from '@mui/material/Paper';
import "./App.css";

const geojsonObject = mapConfig.geojsonObject;
const geojsonObject2 = mapConfig.geojsonObject2;
const markersLonLat = [mapConfig.kansasCityLonLat, mapConfig.blueSpringsLonLat];

function addMarkers(lonLatArray) {
  var iconStyle = new Style({
    image: new Icon({
      anchorXUnits: "fraction",
      anchorYUnits: "pixels",
      src: mapConfig.markerImage32,
    }),
  });
  let features = lonLatArray.map((item) => {
    let feature = new Feature({
      geometry: new Point(fromLonLat(item)),
    });
    feature.setStyle(iconStyle);
    return feature;
  });
  return features;
}

const App = () => {
  const [center, setCenter] = useState([20.5937, 78.9629]);
  const [zoom, setZoom] = useState(3);
  const [dropdownvalue, setDropdownvalue] = useState("")
  const [showLayer1, setShowLayer1] = useState(true);
  const [showLayer2, setShowLayer2] = useState(true);
  const [showMarker, setShowMarker] = useState(false);
  const [show, setShow] = useState(false)
  const [features, setFeatures] = useState(addMarkers(markersLonLat));
  const toggleDrawer = (e) => {
    setShow(true)
  }
  const toggleDrawer1 = (e) => {
    setShow(false)
  }
  const handleChange = (e) => {
    setDropdownvalue(e.target.value);
    console.log(dropdownvalue);
  }
  return (
    <div style={{ flex: 1 }}>
      <div style={{ height: 60, width: '100%', background: '#3877F1' }}>
        <div style={{ height: 60, width: '100%', backgroundRepeat: 'no-repeat', backgroundSize: 'cover', backgroundImage: `url(${Background})` }}>
          <div style={{ height: 60, width: 60, position: 'absolute', right: 0, display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

            <img style={{ width: 25, height: 25 }} src={require('./assets/Vector1.png')}
              onClick={(e) => toggleDrawer()} />
          </div>
        </div>
      </div>
      {/***SELECT BOX */}
      <div style={{ backgroundColor: "#E5E5E5", padding: "4%" }}>
        <select style={{ width: "100%", height: 50, borderRadius: 10 }} value={dropdownvalue} name="dropdownvalue" onChange={(e) => handleChange(e)} >
          <option>None</option>
          <option value="2">Andhra Pradesh</option>
          <option value="1">Daman & Diu</option>
          <option value="3">Goa</option>
          <option value="4">Gujarat</option>
          <option value="5">Karnataka</option>
          <option value="6">Kerala</option>
          <option value="7">Maharashtra</option>
          <option value="8">Odisha</option>
          <option value="9">Puducherry</option>
          <option value="10">Tamil Nadu</option>
          <option value="11">West Bengal</option>
          <option value="12">Andaman & Nicobar Islands</option>
          <option value="13">Lakshadweep</option>
        </select>
      </div>
      <Map center={fromLonLat(center)} zoom={zoom}>
        <Layers>
          <TileLayer source={osm()} zIndex={0} />


          {showLayer2 && (
            <TileLayer source={wms({
              url: 'http://webdcra.ncrmp.gov.in:8080/geoserver/geonode/wms',
              params: { 'layers': 'PowerPlant' },
              serverType: 'geoserver',
              transition: 0,
            })} zIndex={1} />
          )}
          {/* {showMarker && <VectorLayer source={vector({ features })} />} */}
        </Layers>

      </Map>
         {/* -------->> FOR SIDE DRAWER <---------------- */}
         <Drawer anchor="right" open={show} onClose={() => toggleDrawer1()} variant="temporary" elevation={16} PaperProps={{ style: { marginTop: 60 } }}>
        <Paper style={{ display: "flex", flexDirection: "column", width: 320, }}>
          <div className="image-close">
            <span style={{ fontSize: 20, fontWeight: "bold" }}>Layers</span>
            <img src={require('./assets/Vector3.png')} alt="icon" style={{ height: 20, width: 20 }} onClick={() => toggleDrawer1()} />
          </div>
          <div className='drawer-name'>
            <Checkbox
              value={"A"}
              onClick={(value)=>{
console.log(value)
              }}
            /><span className='drawer-text'>Oil And Gas Pipelines</span>
          </div>
          <div className='drawer-name'>
            <Checkbox value={"B"}
            //  onClick={handleCheckboxChange}
            /><span className='drawer-text'>Electricity line</span>
          </div>
          <div className='drawer-name'>
            <Checkbox value={"C"} 
            // onClick={handleCheckboxChange}
            /><span className='drawer-text'>Seaport</span>
          </div>
          <div className='drawer-name'>
            <Checkbox value={"D"} 
            // onClick={handleCheckboxChange} 
            /><span className='drawer-text'>Roads</span>
          </div>
          <div className='drawer-name'>
            <Checkbox value={"E"} 

            // onClick={handleCheckboxChange}

            /><span className='drawer-text'>Railway Station</span>
          </div>
          <div className='drawer-name'>
            <Checkbox
              value={"F"}
              // onClick={handleCheckboxChange}

            /><span className='drawer-text'>Railway Line</span>
          </div>
          <div className='drawer-name'>
            <Checkbox value={"G"}
              // onClick={handleCheckboxChange}
               /><span className='drawer-text'>Bridges</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Airports</span>
          </div>

          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Schools</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Police Station</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Hospitals</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Fire Station</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>CycloneShelter</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Slums</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Residential</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Industrial</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Commercial</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Village Boundary</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Tehsil Boundary</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>District Boundary</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Cone Of Uncertainity</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Forecasted Track Line</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Observed Track Line</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Observed Track Points</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Surge Hazard</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Flood Hazard</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Cyclone Hazard</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Affected Population</span>
          </div>
          <div className='drawer-name'>
            <Checkbox /><span className='drawer-text'>Affected Population Density</span>
          </div>
          <div className='drawer-name1'>
            <Checkbox /><span className='drawer-text'>State Boundary new</span>
          </div>


        </Paper>
      </Drawer>
      {/* <div>
        <input
          type="checkbox"
          checked={showLayer1}
          onChange={(event) => setShowLayer1(event.target.checked)}
        />{" "}
        Johnson County
      </div>
      <div>
        <input
          type="checkbox"
          checked={showLayer2}
          onChange={(event) => setShowLayer2(event.target.checked)}
        />{" "}
        Wyandotte County
      </div>
      <hr />
      <div>
        <input
          type="checkbox"
          checked={showMarker}
          onChange={(event) => setShowMarker(event.target.checked)}
        />{" "}
        Show markers
      </div> */}
    </div>
  );
};

export default App;
